commits.content:
.user-header {

    background: white;
            border-radius: 20px;
            padding: 0.5rem;
            display: flex;
            align-items: center;
            justify-content: space-between;
            margin-bottom: 20px;
    
            .user-info {
                display: flex;
                align-items: center;
                gap: 15px;
    
                .user-image {
                    width: 40px;
                    height: 40px;
                    border-radius: 50%;
                    object-fit: cover;
                }
    
                .user-name {
                    font-size: 18px;
                    font-weight: 600;
                }
    
                .icon {
                    cursor: pointer;
                    background-color: #E27D60;
                    color: white;
                    border-radius: 50%;
                    padding: 10px; 
                    display: inline-flex; 
                    align-items: center;
                    justify-content: center;
                    font-size: 20px; 
                }
                
            }
    
            h1 {
                font-size: 24px;
                background: white;
                border-radius: 20px;
                min-height: 3rem;
                padding: 0.5rem;
            }
        }
