import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrl: './header.component.scss'
})
export class HeaderComponent {
  title: string = 'Tableau de bord'; // Titre par défaut

  // Méthode pour changer dynamiquement le titre en fonction de l'interface
  changeTitle(newTitle: string): void {
    this.title = newTitle;
  }
}
