commits.content:
// Variables pour la cohérence du thème
$primary-color: #E27D60;    // Couleur principale du thème
$secondary-color: #2787C0;  // Couleur secondaire (ex: indicateur 'plus de membres')
$text-color: #333;          // Couleur de texte principale
$light-text-color: #666;   // Couleur de texte plus claire
$background-color: #F1F1F1; // Couleur de fond générale
$white-color: #fff;         // Blanc
$border-color: #ddd;        // Couleur des bordures
$light-gray: #f9f9f9;       // Gris clair (ex: fond de survol)
$medium-gray: #ccc;         // Gris moyen
$dark-gray: #aaa;           // Gris foncé
$error-color: #d9534f;      // Couleur pour les actions de suppression

$base-padding: 20px;        // Espacement interne de base
$base-margin: 20px;         // Marge de base
$border-radius: 10px;       // Rayon de bordure standard
$card-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); // Ombre portée pour les cartes
$transition-speed: 0.3s;    // Vitesse de transition pour les animations

// Conteneur principal
.project-details-container {
  padding: $base-padding;
  font-family: 'Poppins', sans-serif; // Police de caractères
  background-color: $background-color;
  min-height: 95vh; // Hauteur minimale pour occuper l'écran
  box-sizing: border-box; // Inclut padding et border dans la taille totale
}

// Style des onglets de navigation
.tabs {
  display: flex;
  margin-bottom: $base-margin;
  background-color: $white-color;
  border-radius: $border-radius;
  overflow: hidden; // Contient les boutons arrondis
  box-shadow: $card-shadow;

  button {
    flex: 1; // Chaque bouton prend une part égale de l'espace
    padding: 15px 10px; // Espacement interne augmenté
    border: none;
    background-color: $white-color; // Fond par défaut
    color: $light-text-color;
    cursor: pointer;
    font-size: 16px;
    font-weight: 500; // Police légèrement moins grasse
    text-align: center;
    transition: background-color $transition-speed ease, color $transition-speed ease;
    border-bottom: 3px solid transparent; // Bordure inférieure transparente pour l'état actif

    &:hover {
      background-color: $light-gray; // Fond au survol
      color: $primary-color; // Couleur du texte au survol
    }

    // Style de l'onglet actif
    &.active {
      background-color: $white-color; // Garde le fond blanc
      color: $primary-color;
      font-weight: 600; // Police plus grasse pour l'actif
      border-bottom-color: $primary-color; // Affiche la bordure inférieure
    }
  }
}

// Style de base pour le contenu des onglets
.tab-content {
  background-color: $white-color;
  padding: $base-padding;
  border-radius: $border-radius;
  box-shadow: $card-shadow;
  margin-bottom: $base-margin; // Marge en bas si nécessaire
}

// Disposition spécifique pour l'onglet "Détails"
.details-content {
  display: flex;
  gap: $base-margin; // Espace entre les panneaux gauche et droit

  .left-panel,
  .right-panel {
    flex: 1; // Chaque panneau prend la moitié de l'espace
  }

  .right-panel {
    display: flex;
    flex-direction: column; // Empile les cartes verticalement
    gap: $base-margin; // Espace entre les cartes du panneau droit
  }
}

// Style des cartes
.card {
  background-color: $white-color;
  padding: $base-padding;
  border-radius: $border-radius;
  box-shadow: $card-shadow; // Ombre cohérente

  h2 {
    margin-top: 0;
    margin-bottom: 15px;
    color: $primary-color; // Couleur du titre
    font-size: 1.2em;
    border-bottom: 1px solid $border-color; // Ligne de séparation sous le titre
    padding-bottom: 10px;
  }

  p {
    margin: 10px 0;
    color: $light-text-color;
    line-height: 1.6; // Hauteur de ligne pour la lisibilité

    strong {
      color: $text-color; // Met en évidence le label
      margin-right: 5px;
    }
  }

  // Style de la liste (utilisée pour les membres)
  ul {
    list-style: none; // Pas de puces
    padding: 0;
    margin: 0;

    li {
      padding: 8px 0;
      border-bottom: 1px dashed $border-color; // Séparateur en pointillés
      color: $text-color;

      &:last-child {
        border-bottom: none; // Pas de bordure pour le dernier élément
      }
    }
  }
}

// Style du bouton "Modifier"
.edit-btn {
  cursor: pointer;
  background-color: $primary-color;
  border-radius: 20px; // Forme de pilule
  font-weight: 500;
  min-height: 36px; // Hauteur minimale
  padding: 8px 20px; // Espacement interne
  border: none;
  color: $white-color;
  margin-top: 15px; // Marge au-dessus
  transition: background-color $transition-speed ease, transform $transition-speed ease;

  &:hover {
    background-color: darken($primary-color, 10%); // Assombrit au survol
    transform: translateY(-2px); // Léger effet de soulèvement
  }
}

// Style du cercle de progression
.progress-container {
  position: relative;
  width: 120px; // Taille légèrement augmentée
  height: 120px;
  margin: 15px auto; // Centré avec marge

  svg {
    transform: rotate(-90deg); // Fait commencer le cercle en haut
  }

  circle {
    transition: stroke-dashoffset $transition-speed ease; // Animation douce
  }

  .progress-text {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%); // Centrage parfait du texte
    font-size: 1.3em; // Texte plus grand
    font-weight: 600;
    color: $primary-color;
  }
}

// Style de l'en-tête de l'onglet "Phases"
.header {
  display: flex;
  align-items: center;
  justify-content: space-between; // Espace entre le titre et le bouton
  margin-bottom: $base-margin; // Espace sous l'en-tête
  padding-bottom: $base-padding; // Espacement interne en bas
  border-bottom: 1px solid $border-color; // Ligne de séparation

  h2 {
    margin: 0; // Supprime la marge par défaut du titre
    color: $primary-color;
  }
}

// Style du bouton "Ajouter une phase"
.add-phase {
  cursor: pointer;
  background-color: $primary-color;
  border-radius: 25px; // Forme de pilule
  font-weight: 500;
  min-height: 40px;
  padding: 0 25px; // Espacement horizontal
  border: none;
  color: $white-color;
  display: inline-flex; // Permet l'alignement de l'icône et du texte
  align-items: center;
  gap: 8px; // Espace entre l'icône et le texte
  transition: background-color $transition-speed ease, transform $transition-speed ease;

  i {
    font-size: 1.1em; // Icône légèrement plus grande
  }

  &:hover {
    background-color: darken($primary-color, 10%);
    transform: translateY(-2px); // Effet de soulèvement
  }
}

// Style du tableau des phases
.phases-table {
  width: 100%;
  overflow-x: auto; // Permet le défilement horizontal sur petits écrans
  border-radius: $border-radius;
  box-shadow: $card-shadow;
  background-color: $white-color;

  table {
    width: 100%;
    border-collapse: collapse; // Bordures nettes entre les cellules

    th,
    td {
      padding: 15px; // Espacement interne des cellules
      text-align: left; // Alignement à gauche pour la lisibilité
      border-bottom: 1px solid $border-color; // Ligne séparatrice horizontale
      vertical-align: middle; // Alignement vertical au centre
    }

    th {
      background-color: $light-gray; // Fond clair pour l'en-tête
      font-weight: 600; // Texte en gras
      color: $text-color;
    }

    td {
      color: $light-text-color; // Texte plus clair pour les données
    }

    // Centrage spécifique pour certaines colonnes
    th:nth-child(4), // Nombre de Tâches
    td:nth-child(4),
    th:nth-child(5), // Membres
    td:nth-child(5),
    th:nth-child(6), // Actions
    td:nth-child(6) {
      text-align: center;
    }

    tbody tr {
      cursor: pointer; // Indique que la ligne est cliquable
      transition: background-color $transition-speed ease;

      &:hover {
        background-color: lighten($primary-color, 45%); // Effet de survol subtil
      }

      &:last-child td {
        border-bottom: none; // Supprime la bordure pour la dernière ligne
      }
    }
  }
}

// Style de l'affichage des membres dans le tableau
.members-container {
  display: flex;
  align-items: center;
  justify-content: center; // Centre le contenu (images + bouton) dans la cellule
  gap: 10px; // Espace entre le groupe d'images et le bouton '+'
}

.members {
  display: flex;
  align-items: center;
  gap: -10px; // Fait se chevaucher légèrement les images

  .member-img {
    width: 32px;
    height: 32px;
    border-radius: 50%; // Image ronde
    object-fit: cover; // Assure que l'image couvre la zone
    border: 2px solid $white-color; // Bordure blanche pour séparer les images qui se chevauchent
    display: inline-block;
    background-color: $light-gray; // Fond de secours si l'image ne charge pas
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2); // Ombre subtile pour la profondeur
  }

  // Style de l'indicateur "+X"
  .more-members {
    width: 32px;
    height: 32px;
    border-radius: 50%;
    background-color: $dark-gray; // Fond gris neutre
    color: $white-color;
    display: inline-flex; // Utilise inline-flex pour un meilleur contrôle
    align-items: center;
    justify-content: center;
    font-size: 12px; // Petite taille de police
    font-weight: 600;
    border: 2px solid $white-color;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);
    margin-left: -10px; // Chevauchement avec la dernière image affichée
    z-index: 1; // S'assure qu'il est au-dessus de l'image précédente
    flex-shrink: 0; // Empêche le rétrécissement si l'espace manque
  }
}

// Style du bouton "+" pour ajouter un membre
.add-member-btn {
  background: none;
  border: none;
  cursor: pointer;
  padding: 0; // Supprime l'espacement par défaut
  line-height: 1; // Assure un bon alignement vertical de l'icône

  .icon {
    font-size: 24px; // Icône plus grande
    color: $secondary-color; // Couleur bleue
    transition: color $transition-speed ease;

    &:hover {
      color: darken($secondary-color, 15%); // Assombrit au survol
    }
  }
}

// Style général pour les boutons d'icônes (Modifier/Supprimer dans le tableau)
button[mat-icon-button] {
  background: none;
  border: none;
  cursor: pointer;
  padding: 5px; // Ajoute une zone cliquable autour de l'icône
  border-radius: 50%; // Rend le fond de survol circulaire
  transition: background-color $transition-speed ease;

  &:hover {
    background-color: rgba($dark-gray, 0.1); // Fond très léger au survol
  }

  // Style de base pour les icônes dans ces boutons
  .icon {
    font-size: 18px;
    color: $text-color; // Couleur par défaut de l'icône
    transition: color $transition-speed ease;
    vertical-align: middle; // Aide à l'alignement vertical
  }

  // Changement de couleur de l'icône au survol du bouton
  &:hover .icon {
    color: $primary-color;
  }

  // Style spécifique pour l'icône de suppression (poubelle)
  .bi-trash {
    color: $error-color; // Rouge pour indiquer une action destructive
  }

  &:hover .bi-trash {
    color: darken($error-color, 15%); // Rouge plus foncé au survol
  }
}

// Message affiché s'il n'y a pas de phases
div[ *ngIf="project.phases.length === 0"] { // Sélecteur plus spécifique si nécessaire
  text-align: center;
  padding: $base-padding * 2; // Plus d'espacement
  color: $light-text-color;
  font-style: italic; // Texte en italique
}


// Ajustements pour les écrans plus petits (tablettes)
@media (max-width: 768px) {
  .details-content {
    flex-direction: column; // Empile les panneaux gauche et droit
  }

  .header {
    flex-direction: column; // Empile le titre et le bouton
    align-items: flex-start; // Aligne les éléments à gauche
    gap: 15px; // Espace entre le titre et le bouton

    .add-phase {
      margin-left: 0; // Supprime la marge automatique
      align-self: flex-start; // Aligne le bouton à gauche
    }
  }

  .phases-table {
    th, td {
      padding: 12px 10px; // Réduit légèrement l'espacement
      font-size: 0.9em; // Réduit la taille de la police
    }

    // Optionnel: Ajuster l'alignement centré si l'espace est limité
    // th:nth-child(5), // Membres
    // td:nth-child(5) {
    //   text-align: left;
    // }
  }

  .members-container {
     justify-content: flex-start; // Aligne les membres à gauche sur petits écrans
  }
}

// Ajustements pour les écrans très petits (mobiles)
@media (max-width: 480px) {
  .tabs button {
    font-size: 14px; // Texte des onglets plus petit
    padding: 12px 5px;
  }

  .card h2 {
    font-size: 1.1em; // Titres de carte plus petits
  }

  // Boutons pleine largeur pour une meilleure accessibilité mobile
  .edit-btn, .add-phase {
    width: 100%;
    text-align: center;
    padding: 10px 15px;
    justify-content: center; // Centre le contenu (icône + texte)
  }

  .phases-table {
    font-size: 0.85em; // Police encore plus petite pour le tableau

    th, td {
      padding: 10px 8px; // Espacement réduit
    }
  }

  // Optionnel: Masquer des colonnes moins critiques sur mobile
  // Exemple pour masquer la description :
  // .phases-table th:nth-child(2),
  // .phases-table td:nth-child(2) {
  //   display: none;
  // }
}

// Style pour les boîtes de dialogue (conservé de l'original, à adapter si besoin)
.custom-dialog-container {
  padding: 20px;
  border-radius: 8px;
}

@media (max-width: 768px) {
  .custom-dialog-container {
    width: 90% !important;
    max-width: 90% !important;
    left: 5% !important; // Centre horizontalement
    top: 10% !important; // Position verticale
  }
}

@media (max-width: 480px) {
  .custom-dialog-container {
    width: 95% !important;
    max-width: 95% !important;
    left: 2.5% !important; // Centre horizontalement
    top: 10% !important; // Position verticale
  }
}
