import { Component } from '@angular/core';

@Component({
  selector: 'app-project-form',
  templateUrl: './project-form.component.html',
  styleUrl: './project-form.component.scss'
})
export class ProjectFormComponent implements OnInit {
  projectForm: FormGroup;
  isLoading = false;
  errorMessage: string | null = null;

  constructor(
    private fb: FormBuilder,
    private projectService: ProjectService,
    private authService: AuthService, // Injectez AuthService
    public dialogRef: MatDialogRef<ProjectFormComponent>,
    // @Inject(MAT_DIALOG_DATA) public data: any // Injectez si vous passez des données à la modale
  ) {
    this.projectForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      description: ['', Validators.maxLength(500)] // Description optionnelle, max 500 caractères
    });
  }

  ngOnInit(): void { }

  get name() { return this.projectForm.get('name'); }
  get description() { return this.projectForm.get('description'); }

  saveProject(): void {
    if (this.projectForm.invalid || this.isLoading) {
      this.projectForm.markAllAsTouched(); // Marque tous les champs comme touchés pour afficher les erreurs
      return;
    }

    this.isLoading = true;
    this.errorMessage = null;

    const idCompany = localStorage.getItem("idAgence");
    const idAdmin = this.authService.getCurrentUserId(); // Obtenez l'ID de l'admin connecté

    if (!idCompany || !idAdmin) {
      this.errorMessage = "Impossible de récupérer les informations nécessaires (Agence ou Administrateur).";
      this.isLoading = false;
      return;
    }

    const projectData = {
      name: this.name?.value,
      description: this.description?.value,
      idCompany: idCompany,
      idAdmin: idAdmin
    };

    this.projectService.createProject(projectData).subscribe({
      next: (newProject) => {
        this.isLoading = false;
        console.log('Projet créé:', newProject);
        this.dialogRef.close(newProject); // Ferme la modale et renvoie le nouveau projet
      },
      error: (err) => {
        this.isLoading = false;
        this.errorMessage = err.message || 'Une erreur est survenue lors de la création du projet.';
        console.error('Erreur création projet:', err);
      }
    });
  }

  cancel(): void {
    this.dialogRef.close(); // Ferme simplement la modale sans renvoyer de données
  }
}
