--- 
+++ 
@@ -0,0 +1,342 @@
+$primary-color: #41B3A3;
+$accent-color: #E27D60;
+$warn-color: #F44336; // For delete actions
+$success-color: #4CAF50; // For save/add actions
+$cancel-color: #6c757d; // For cancel actions
+
+$text-color-primary: #333;
+$text-color-secondary: #666;
+$text-color-light: #fff;
+
+$background-color: #f7f9fc;
+$container-background: #fff;
+$border-color: #e0e0e0;
+$border-radius: 8px;
+$card-shadow: 0 4px 12px rgba(0, 0, 0, 0.08);
+
+$priority-high-color: #D9534F; // Adjusted warn color
+$priority-medium-color: #E9A28A; // Adjusted accent color
+$priority-low-color: #6CBBAF;
+$priority-high-bg: lighten($priority-high-color, 40%); // Corrected lighten syntax if needed elsewhere
+$priority-medium-bg: lighten($priority-medium-color, 35%); // Corrected lighten syntax if needed elsewhere
+$priority-low-bg: lighten($priority-low-color, 40%);
+
+
+.task-details-container {
+  display: flex; // Rendre le conteneur principal flex
+  flex-direction: column;
+  background: white;
+  border-radius: 12px;
+  padding: 24px;
+  width: 80vw;
+  max-width: 1100px;
+  max-height: 90vh;
+  overflow-y: auto;
+  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
+
+  .task-header {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    margin-bottom: 20px;
+    padding-bottom: 15px;
+    border-bottom: 1px solid $border-color;
+
+    h2 {
+      margin: 0;
+      color: $primary-color;
+      font-size: 1.8rem;
+      overflow-wrap: break-word;
+    }
+
+    .close-btn {
+      color: $text-color-secondary;
+      transition: all 0.2s;
+
+      &:hover {
+        color: $warn-color;
+        transform: scale(1.1);
+      }
+    }
+  }
+
+  .task-content {
+    display: flex;
+    flex: 1; // Permet à cette zone de s'étendre pour remplir l'espace vertical restant
+    //overflow-y: hidden;
+    gap: 30px;
+    min-height: 0;
+    .task-left-column {
+      flex: 1;
+      min-width: 300px;
+      overflow-y: auto; 
+      overflow-x: hidden;
+      min-height: 0;
+    }
+
+    .task-right-column {
+      flex: 2;
+      overflow-y: auto; // Allow this column to scroll if needed
+      min-height: 0;
+      overflow-x: hidden; 
+    }
+  }
+
+  .task-section {
+    margin-bottom: 25px;
+
+    h3 {
+      color: $primary-color;
+      margin-top: 0;
+      margin-bottom: 15px;
+      font-size: 1.2rem;
+      display: flex;
+      align-items: center;
+      gap: 8px;
+
+      &::before {
+        content: '';
+        display: block;
+        width: 8px;
+        height: 8px;
+        background: $primary-color;
+        border-radius: 50%;
+      }
+    }
+  }
+
+  .priority-badge {
+    display: inline-flex;
+    align-items: center;
+    padding: 6px 12px;
+    border-radius: 20px;
+    font-weight: 600;
+    font-size: 0.85rem;
+    margin-bottom: 15px;
+
+    &.high {
+      background-color: rgba($priority-high-color, 0.1);
+      color: $priority-high-color;
+      border: 1px solid rgba($priority-high-color, 0.3);
+    }
+
+    &.medium {
+      background-color: rgba($priority-medium-color, 0.1);
+      color: $priority-medium-color;
+      border: 1px solid rgba($priority-medium-color, 0.3);
+    }
+
+    &.low {
+      background-color: rgba($priority-low-color, 0.1);
+      color: $priority-low-color;
+      border: 1px solid rgba($priority-low-color, 0.3);
+    }
+  }
+
+  .date-range {
+    display: flex;
+    gap: 20px;
+
+    .date-item {
+      label {
+        display: block;
+        font-size: 0.8rem;
+        color: $text-color-secondary;
