.agence-container {
    padding: 20px;
    font-family: 'Poppins', sans-serif;
    background: #F1F1F1;
    height: 100vh;
  
    .container {
      background: white;
      border-radius: 20px;
      padding: 0.5rem;
      align-items: center;
      justify-content: space-between;
    }
  
    .agence-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      flex-wrap: wrap;
      margin-bottom: 20px;
      gap: 10px;
    }
  
    /* Search Bar */
    .input-container {
      position: relative;
      display: flex;
      align-items: center;
      flex: 1;
    }
  
    .input-container input {
      background: #d6d6d6;
      padding-left: 30px;
      border: none;
      border-radius: 20px;
      min-height: 40px;
      width: 100%;
    }
  
    .input-container .icon {
      position: absolute;
      left: 10px;
      font-size: 20px;
      color: #ccc;
    }
  
    /* Date */
    .current-date {
      font-size: 1.2rem;
      background-color: #ccc;
      border-radius: 30px;
      padding: 10px;
      white-space: nowrap;
    }
  
    /* Add Agency Button */
    .add-agency {
      cursor: pointer;
      background: #E27D60;
      border-radius: 40px;
      font-weight: bold;
      min-height: 3rem;
      border: none;
      color: white;
      padding: 0 20px;
      display: flex;
      align-items: center;
  
      i {
        font-size: 1rem;
        font-weight: bold;
        margin-right: 5px;
      }
    }
  
    /* Agency Table */
    .agence-display {
      flex-grow: 1;
      display: flex;
      flex-direction: column;
      width: 100%;
    }
  
    .agence-table {
      width: 100%;
      overflow: hidden;
      border-radius: 12px;
      box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
      background: white;
  
      table {
        width: 100%;
        border-radius: 12px;
        overflow: hidden;
        border-collapse: collapse;
      }
  
      th, td {
        padding: 15px;
        text-align: left;
        border-bottom: 1px solid #ddd;
      }
  
      th {
        background-color: #f4f4f4;
        font-weight: 600;
      }
  
      tr {
        cursor: pointer;
        transition: background-color 0.3s ease-in-out;
  
        &:hover {
          background-color: #f9f9f9;
        }
      }
  
      .even {
        background-color: #f9f9f9;
      }
  
      .odd {
        background-color: #ffffff;
      }
  
      .actions {
        display: flex;
        gap: 10px;
        justify-content: center;
  
        button {
          border: none;
          background: none;
          cursor: pointer;
          font-size: 1.2rem;
          padding: 5px;
          border-radius: 50%;
          width: 35px;
          height: 35px;
          display: flex;
          align-items: center;
          justify-content: center;
          transition: all 0.2s;
  
          &:hover {
            transform: scale(1.1);
          }
        }
  
        .edit-btn {
          color: #4CAF50;
  
          &:hover {
            background-color: rgba(76, 175, 80, 0.1);
          }
        }
  
        .delete-btn {
          color: #F44336;
  
          &:hover {
            background-color: rgba(244, 67, 54, 0.1);
          }
        }
      }
    }
  
    /* Responsive Design */
    @media (max-width: 1024px) {
      .agence-header {
        flex-direction: column;
        align-items: center;
        gap: 15px;
      }
  
      .add-agency {
        width: 100%;
        justify-content: center;
      }
  
      .agence-table {
        overflow-x: auto;
  
        table {
          min-width: 800px;
        }
      }
    }
  }
