commits.content:
.dashboard-container {
    font-family: 'Poppins', sans-serif;
    padding: 20px;
    background: #F1F1F1;
    .user-header {

        background: white;
                border-radius: 20px;
                padding: 0.5rem;
                display: flex;
                align-items: center;
                justify-content: space-between;
                margin-bottom: 20px;
        
                .user-info {
                    display: flex;
                    align-items: center;
                    gap: 15px;
        
                    .user-image {
                        width: 40px;
                        height: 40px;
                        border-radius: 50%;
                        object-fit: cover;
                    }
        
                    .user-name {
                        font-size: 18px;
                        font-weight: 600;
                    }
        
                    .icon {
                        cursor: pointer;
                        background-color: #E27D60;
                        color: white;
                        border-radius: 50%;
                        padding: 10px; 
                        display: inline-flex; 
                        align-items: center;
                        justify-content: center;
                        font-size: 20px; 
                    }
                    
                }
        
                h1 {
                    font-size: 24px;
                    background: white;
                    border-radius: 20px;
                    min-height: 3rem;
                    padding: 0.5rem;
                }
            }
    h2 {
      font-size: 20px;
      font-weight: 600;
    }
  
    /* Task Summary */
    .task-summary {
      background: white;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
      margin-bottom: 20px;
      position: relative;
  
      .add-project {
        position: absolute;
        cursor: pointer;
        top: 20px;
        right: 20px;
        background: #E27D60  ;
        border-radius: 40px;
        font-weight: bold;
        min-height: 3rem;
        border: none;
        color: white;
        i{
            font-size: 0.8rem;  /* Augmente la taille de l'icône */
    font-weight: bold;  /* Tentative pour ajouter du poids si l'icône supporte cela */
        }
      }
  
      .task-cards {
        display: flex;
        gap: 20px;
        margin-top: 20px;
  
        .task-card {
          flex: 1;
          padding: 20px;
          border-radius: 10px;
          color: white;
          text-align: center;
          font-weight: 600;
          box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          gap: 10px;
  
          i {
            font-size: 30px;
          }
  
          &.pending {
            background: rgb(185, 160, 160);
          }
          &.in-progress {
            background: #99d;
          }
          &.completed {
            background: #ffdb58;
          }
        }
      }
    }
  
    /* Task Activity */
    .task-activity {
      background: white;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
      margin-bottom: 20px;
      .task-stats {
        display: flex;
        gap: 20px;
        justify-content: space-between;
        margin-top: 20px;
  
        .stat {
          background: #fff;
          padding: 15px;
          border-radius: 10px;
          flex: 1;
          text-align: center;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
          
          i {
            font-size: 25px;
            color: #ffc107;
          }
          
          p {
            font-size: 14px;
            color: #2E2E2E;
          }
  
          h3 {
            font-size: 18px;
            margin: 5px 0;
          }
        }
      }
    }  
  .project-list {
   
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
    background: white;
    padding: 20px;
    border-radius: 10px;

  
  table {
    width: 100%;
    border-collapse: collapse;

    th, td {
      padding: 12px;
      border-bottom: 1px solid #ddd;
      text-align: left;
    }

    .progress-bar {
      background: #eee;
      border-radius: 10px;
      height: 10px;
      width: 100%;
      position: relative;

      .progress {
        height: 100%;
        border-radius: 10px;
        background: #3498db;
      }
    }
  }
.icon-spacing{
    margin-right: 10px;
}
.bi{
cursor: pointer;
}}}
