+export interface ProjectElement {
+  name: string;
+  statusClass: string;
+  statusText: string;
+  progress: number;
+}
+
+@Component({
+  selector: 'app-super-admin-dashboard',
+  templateUrl: './super-admin-dashboard.component.html',
+  styleUrls: ['./super-admin-dashboard.component.scss']
+})
+export class SuperAdminDashboardComponent implements OnInit {
+
+  // Données d'exemple pour mat-table
+  displayedColumns: string[] = ['name', 'status', 'progress', 'actions'];
+  dataSource: ProjectElement[] = [
+    { name: 'Rénovation Villa "Horizon"', statusClass: 'status-in-progress', statusText: 'En cours', progress: 60 },
+    { name: 'Construction Immeuble "Le Central"', statusClass: 'status-completed', statusText: 'Terminé', progress: 100 },
+    { name: 'Étude Centre Commercial "Agora"', statusClass: 'status-pending', statusText: 'En attente', progress: 15 },
+    // Ajoutez d'autres projets ici
+  ];
+
+  constructor() { }
+
+  ngOnInit(): void {
+    // Initialisation du graphique (si vous utilisez Chart.js ou autre)
+    this.initializeChart();
+  }
+
+  initializeChart(): void {
+    // Votre logique d'initialisation de graphique ici
+    // Exemple simple (nécessite d'importer Chart depuis 'chart.js')
+    /*
+    const ctx = document.getElementById('taskChart') as HTMLCanvasElement;
+    if (ctx) {
+      new Chart(ctx, {
+        type: 'line', // ou 'bar', 'doughnut', etc.
+        data: {
+          labels: ['Jan', 'Fév', 'Mar', 'Avr', 'Mai', 'Juin'],
+          datasets: [{
+            label: 'Tâches Complétées',
+            data: [12, 19, 3, 5, 2, 3],
+            borderColor: 'var(--primary-color)',
+            tension: 0.1
+          }]
+        },
+        options: {
+          responsive: true,
+          maintainAspectRatio: false // Important pour le conteneur flex/grid
+        }
+      });
+    }
+    */
+  }
+
+  // Méthodes pour les actions (nouvelle agence, voir/modifier projet)
+  addAgency(): void {
+    console.log('Ajouter une nouvelle agence');
+    // Logique d'ajout...
+  }
+
+  viewProject(project: ProjectElement): void {
+    console.log('Voir projet:', project.name);
+    // Logique de navigation ou affichage modal...
+  }
+
+  editProject(project: ProjectElement): void {
+    console.log('Modifier projet:', project.name);
+    // Logique d'édition...
+  }
+}
+ngAfterViewInit(): void {
+    this.createChart();
+  }
+
+  createChart() {
+    const ctx = document.getElementById('taskChart') as HTMLCanvasElement;
+    if (ctx) {
+      new Chart(ctx, {
+        type: 'bar',
+        data: {
+          labels: ['Jan', 'Fév', 'Mar', 'Avr', 'Mai', 'Juin'],
+          datasets: [{
+            label: 'Tâches complétées',
+            data: [50, 60, 75, 90, 110, 130],
+            backgroundColor: '#3498db',
+            borderColor: '#2980b9',
+            borderWidth: 1
+          }]
+        },
+        options: {
+          responsive: true,
+          scales: {
+            y: { beginAtZero: true }
+          }
+        }
+      });
+    }
+  }
+}

