.user-form-container {
    padding: 20px;
    font-family: 'Poppins', sans-serif;
  
    h2 {
      color: #E27D60;
      margin-bottom: 20px;
      text-align: center;
    }
  
    .form-group {
      margin-bottom: 20px;
  
      label {
        display: block;
        margin-bottom: 5px;
        font-weight: 500;
        color: #555;
      }
  
      input, select {
        width: 100%;
        padding: 10px;
        border: 1px solid #ddd;
        border-radius: 4px;
        font-size: 16px;
  
        &:focus {
          outline: none;
          border-color: #E27D60;
        }
      }
  
      .error-message {
        color: #F44336;
        font-size: 12px;
        margin-top: 5px;
      }
    }
  
    .form-actions {
      display: flex;
      justify-content: flex-end;
      gap: 10px;
      margin-top: 30px;
  
      button {
        padding: 8px 20px;
        border-radius: 4px;
        cursor: pointer;
        font-weight: 500;
        transition: all 0.3s;
  
        &.cancel-btn {
          background: #f5f5f5;
          border: 1px solid #ddd;
          color: #333;
  
          &:hover {
            background: #e0e0e0;
          }
        }
  
        &.submit-btn {
          background: #E27D60;
          color: white;
          border: none;
  
          &:hover {
            background: darken(#E27D60, 10%);
          }
  
          &:disabled {
            background: #cccccc;
            cursor: not-allowed;
          }
        }
      }
    }
  }
